function hasClass(s,n){return s.className&&new RegExp("(^|\\s)"+n+"(\\s|$)").test(s.className)}for(var plan_links=document.getElementById("plan-nav").getElementsByTagName("a"),plans=document.getElementById("plans-wrapper").children,i=0;i<plan_links.length;i++)plan_links[i].onclick=function(){for(var s=this.classList[0],n=0;n<plan_links.length;n++)plan_links[n].classList.remove("active");setTimeout(function(){for(var n=0;n<plan_links.length;n++)hasClass(plan_links[n],s)===!0&&plan_links[n].classList.add("active")},10);for(var a=0;a<plans.length;a++)plans[a].classList.remove("active");return setTimeout(function(){for(var n=0;n<plans.length;n++)plans[n].style.display="none",hasClass(plans[n],s)===!0&&(plans[n].style.display="block",plans[n].classList.add("active"))},300),!1};